File selectorsFile = new File("/Users/user/IdeaProjects/selenium-cucumber/src/test/resources/files/selectors.txt")selectorsFile.splitEachLine('\r\n') {String a  ->    str = a.tokenize('|');    String name    String sel    str.each {        if (it.trim().startsWith('xpath')) {            sel = it.replace("xpath=", "")            if (sel) {                def m = sel =~ /'[a-z\s|A-Z]+'/                if(sel && m){                    String title = m[0].toString()                    if(title.endsWith(']')){title=title.replace("]","")}                    if(title.startsWith("'")){title=title.replace("'","")}                    if(title.endsWith("'")){title=title.replace("'","")}                    titleValue=title                    if(title.contains(" ")){title=title.replace(" ","")}                    println("${title}=${titleValue}")                    //println("${title} =${sel}")                }            }        }    }}