package com.test.step.defintions;import org.apache.log4j.Logger;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.support.ui.ExpectedConditions;import org.openqa.selenium.support.ui.WebDriverWait;import com.selenium.configure.environment.PropertiesHandler;public class WaitCommands{    private static WebDriver driver;    private static Logger log = Logger.getLogger(ClickSteps.class);    public WaitCommands(){driver= Hooks.driver;}    public static void waitForElementVisible(By element) throws Exception {        log.info("Waiting for Element to be Visible!");        WebDriverWait w = new WebDriverWait(driver, 15);        w.until(ExpectedConditions.visibilityOfElementLocated(element));    }    public static void waitForClick(By element) throws Exception {        log.info("Waiting for the element to be clickable!");        WebDriverWait w = new WebDriverWait(driver, 15);        w.until(ExpectedConditions.elementToBeClickable(element));    }}